import { useLocation, Navigate, Outlet } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { useContext } from "react";

const RequireAuth = ({ allowedRoles }) => {
    const{authToken,authRole} = useContext(AuthContext);

    const [token,setToken] = authToken
    const [role,setRole] = authRole
    const location = useLocation();
    // ?.find(role => allowedRoles?.includes(role))
    // console.log(token?.roles.find(role => allowedRoles?.includes(role)))

    const getRole = async ()=>{
        const response = await axios.get('/user/me',
                {
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: "Bearer " + token
                    },
                }
            ).then((response => {
                if (!response) {
                    setToken(null)
                }
                console.log();
                setRole(response?.data?.role)
                console.log(role);
                localStorage.setItem("OauthToken", token);
            }))
    }
    
    return (
        role.find(role => allowedRoles?.includes(role))
            ?<Outlet />
            : role?.user
                ? <Navigate to="/unauthorized" state={{ from: location }} replace />
                : <Navigate to="/login" state={{ from: location }} replace />
    );
}

export default RequireAuth;
